import React from "react";

const CakeInfoModal = ({ cake, onClose }) => {
  return (
    <div className="modal">
      <div className="modal-content">
        <span className="close" onClick={onClose}>
          &times;
        </span>
        <h2>{cake.title}</h2>
        <p>ID: {cake.id}</p>
        <p>Difficulty: {cake.difficulty}</p>
        <p>Portion: {cake.portion}</p>
        <p>Time: {cake.time}</p>
        <p>Description: {cake.description}</p>
        <h3>Method:</h3>
        <ol>
          {cake.method.map((step, index) => (
            <li key={index}>{step}</li>
          ))}
        </ol>
        <img src={cake.image} alt={cake.title} className="img-fluid" />
      </div>
    </div>
  );
};

export default CakeInfoModal;
